mapper.select:
 	SELECT id,account,`name`,operationtime,content,ip,op_type FROM sys_log WHERE 1=1 
	
	
		AND op_type = #{opType}
	
	
		AND account LIKE '%${account}%'
	
	
		AND `name` LIKE '%${name}%'
	
	
		AND operationtime >= #{startTime}
	
	
		AND operationtime <= #{endTime}
	
	ORDER BY id desc
	LIMIT #{start},#{size}

 		SELECT COUNT(1) FROM sys_log WHERE 1=1 
		
			AND op_type = #{opType}
		
		
			AND account LIKE '%${account}%'
		
		
			AND `name` LIKE '%${name}%'
		
		
			AND operationtime >= #{startTime}
		
		
			AND operationtime <= #{endTime}
